import React, { Component } from 'react';
import ParticlesBg from 'particles-bg';
import Clarifai from 'clarifai';
import Navigation from './components/Navigation/Navigation';
import Signin from './components/Signin/Signin';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import './App.css';



// const app = new Clarifai.App({
//     apiKey: '3784dc030efe4f808ee15a8556a38831'
// });

const returnClarifaiRequestOptions = (imageUrl) => {
    const PAT = '3784dc030efe4f808ee15a8556a38831';
    const USER_ID = 'zaugust48';
    const APP_ID = 'Imagerecognition';
    const MODEL_ID = 'face-detection';
    const IMAGE_URL = imageUrl;

     const raw = JSON.stringify({
        "user_app_id": {
            "user_id": USER_ID,
            "app_id": APP_ID
        },
        "inputs": [
            {
                "data": {
                    "image": {
                        "url": IMAGE_URL
                        // "base64": IMAGE_BYTES_STRING
                    }
                }
            }
        ]
    });
    const requestOptions= {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Authorization': 'Key ' + PAT
        },
        body: raw

    };
    return requestOptions
}



class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {},
    }
  }

    calculateFaceLocation = (data) => {
       const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box
       const image = document.getElementById('inputimage');
       const width = Number(image.width);
       const height = Number(image.height);
       return {
        leftCol: clarifaiFace.left_col * width,
        topRow: clarifaiDace.top_row * height,
        rightCol: width - (clarifaiFace.right_col),
        bottomRow: height - (clarifaiFace.bottom_row * height)

       }
    }

    displayFaceBox = (box) => {
        this.setState({box: box});
    }

    onInputChange = (event) => {
        this.setState({input: event.target.value});
    }

    onButtonSubmit = () => {
        this.setState({imageUrl: this.state.input});
        fetch("https://api.clarifai.com/v2/models/" + 'face-detection' + "/outputs", returnClarifaiRequestOptions(this.state.input))
            .then(response => response.json())
            .then(response => {
                console.log('hi', response)
                if (response) {
                    fetch('http://localhost:3000/image', {
                        method: 'put',
                        headers: {'Content-Type': 'application/json'},
                        body: JSON.stringify({
                            id: this.state.user.id
                        })
                    })
                        .then(response => response.json())
                        .then(count => {
                            this.setState(Object.assign(this.state.user, { entries: count}))
                        })

            }
            this.displayFaceBox(this.calculateFaceLocation(response))
          })
          .catch(err => console.log(err));
    }


                    //     .then(result => {

                    //         const regions = result.outputs[0].data.regions;

                    //         regions.forEach(region => {
                    //         // Accessing and rounding the bounding box values
                    //             const boundingBox = region.region_info.bounding_box;
                    //             const topRow = boundingBox.top_row.toFixed(3);
                    //             const leftCol = boundingBox.left_col.toFixed(3);
                    //             const bottomRow = boundingBox.bottom_row.toFixed(3);
                    //             const rightCol = boundingBox.right_col.toFixed(3);

                    //             region.data.concepts.forEach(concept => {
                    //             // Accessing and rounding the concept value
                    //                 const name = concept.name;
                    //                 const value = concept.value.toFixed(4);

                    //                 console.log(`${name}: ${value} BBox: ${topRow}, ${leftCol}, ${bottomRow}, ${rightCol}`);

                    //             });
                    //         });

                    //     })
                    // .catch(error => console.log('error', error))


    render() {
        return (
            <div className="App">
                <Signin />                
                <Navigation />
                <ParticlesBg className='particles' num={200} type="cobweb" bg={true} />
                <Logo />
                <Rank />
                <ImageLinkForm 
                    onInputChange={this.onInputChange} 
                    onButtonSubmit={this.onButtonSubmit}
                />               
                <FaceRecognition box={this.state.box} imageUrl={this.state.imageUrl}/>       
           </div>
        );
    }
}
export default App;
