import React from 'react';

class Signin extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      signInEmail: '',
      signInPassword: ''
    }
  }

  onEmailChange = (event) => {
    this.setState({signInEmail: event.target.value})
  }

  onPasswordChange = (event) => {
    this.setState({signInPassword: event.target.value})
  }

  onSubmitSignIn = () => {
  fetch('http://localhost:3000/smart-brain-api/controllers/signin', {
    method: 'post',
    headers: {'Content-Type': 'application/json'},
    body: JSON.stringify({
      email: this.state.signInEmail,
      password: this.state.signInPassword
    })
  })
  .then(response => {
    // Check if the response status code is OK (200-299)
    if (!response.ok) {
      // If not, throw an error with the status text
      throw new Error(`Network response was not ok: ${response.statusText}`);
    }
    return response.json();
  })
  .then(user => {
    // Check for a specific property to confirm user is retrieved
    if (user.id) {
      this.props.loadUser(user);
      this.props.onRouteChange('home');
    } else {
      // Handle the scenario when user credentials are wrong
      console.log('Unable to sign in');
      // Potentially reset state or inform the user
    }
  })
  .catch(error => {
    // Catch any other errors and log them
    console.error(error);
  });
}


